import React, { useEffect, useState } from 'react';
import './RecentVideo.css';

const API_KEY = process.env.REACT_APP_YOUTUBE_KEY;

const RecentVideo = ({ channelId, title }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [videoId, setVideoId] = useState(null);

  useEffect(() => {
    setIsVisible(true);

    fetch(
      `https://www.googleapis.com/youtube/v3/search?key=${API_KEY}&channelId=${channelId}&part=snippet,id&order=date&maxResults=1`
    )
      .then(res => res.json())
      .then(data => {
        const id = data.items?.[0]?.id?.videoId;
        if (id) setVideoId(id);
      })
      .catch(err => console.error('Failed to fetch latest video:', err));
  }, [channelId]);

  // Prevent rendering iframe if videoId is not loaded yet
  if (!videoId) return null;

  return (
    <div className={`recent-video ${isVisible ? 'fade-in-up' : ''}`}>
      <h2 className="section-title">{title}</h2>
      <div className="video-container">
        <iframe
          width="100%"
          height="500"
          src={`https://www.youtube.com/embed/${videoId}`}
          title={title}
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
          allowFullScreen
        />
      </div>
    </div>
  );
};

export default RecentVideo;
